document.addEventListener("DOMContentLoaded",(function(){const t=document.querySelectorAll(".tab"),e=document.querySelectorAll(".tab-page");t.forEach((a=>{a.addEventListener("click",(()=>{const c=a.getAttribute("data-target");t.forEach((t=>t.classList.remove("active"))),e.forEach((t=>{t.classList.remove("active"),t.style.display="none"})),a.classList.add("active");const s=document.getElementById(c);s.style.display="block",setTimeout((()=>{s.classList.add("active")}),10)}))}))}));