document.addEventListener("DOMContentLoaded",function(){let c=document.querySelectorAll(".tab"),l=document.querySelectorAll(".tab-page");c.forEach(a=>{a.addEventListener("click",()=>{var e=a.getAttribute("data-target");c.forEach(e=>e.classList.remove("active")),l.forEach(e=>{e.classList.remove("active"),e.style.display="none"}),a.classList.add("active");let t=document.getElementById(e);t.style.display="block",setTimeout(()=>{t.classList.add("active")},10)})})});